{
	status = "Passed"
	return status.toString()
}

return status.toString()

def programs = context.expand( '${programs#Response#$.ResponseStatus}' )
def organization = context.expand( '${organization#Response#$.ResponseStatus}' )
def approved = context.expand( '${approved#Response#$.ResponseStatus}' )
def Unapproved = context.expand( '${Unapproved#Response#$.ResponseStatus}' )
def ApproveClass = context.expand( '${ApproveClass#Response#$.ResponseStatus}' )




if ((programs=="OK") && (organization=="OK") && (approved=="OK") && (Unapproved=="OK") && (ApproveClass=="OK"))
{
	def res = "Passed"
	return res.toString()
}

else
{
	def res="Failed"
	return res.toString()
}
