con:configuration.script:
def workspace = context.expand('${workspaceDir}')
//return workspace
def project = testRunner.testCase.testSuite.project.name
def contentLibraryData = context.expand( '${#Project#contentLibraryData}' )

if(contentLibraryData=='win')
{
	def path = workspace+"\\V1\\data\\"+project+"Data.xls"

	return path
}
else if(contentLibraryData=='mac')
{
	def path = workspace+"/V1/data/"+project+"Data.xls"
	return path
}
else
{
	return contentLibraryData
}

